mapper.update:
UPDATE food
SET foodname=#{foodname}, contents=#{contents}, phone=#{phone}, local=#{local}, fname=#{fname}, zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}
WHERE foodno=#{foodno}

mapper.select:
select foodno, foodname, contents, phone, viewcnt, wdate, local, fname, r from ( select foodno, foodname, contents, phone, viewcnt, wdate, local, fname, rownum r from( select foodno, foodname, contents, phone, viewcnt, to_char(wdate,'yyyy/mm/dd') as wdate, local, fname
from(select * from food
foodname like '%'||#{word}||'%' or contents like '%'||#{word}||'%'
)
local = #{col}
from food
foodname like '%'||#{word}||'%' or contents like '%'||#{word}||'%'
order by viewcnt desc
order by foodno desc
)
)where r >= #{sno} and r <= #{eno}
