mapper.select:
SELECT id, mname, tel, email, zipcode, address1, address2,
fname, point, r
from(
	SELECT id, mname, tel, email, zipcode, address1, address2,
	fname, point, rownum r
	from(
		SELECT id, mname, tel, email, zipcode, address1,
		address2, fname, point
		FROM fmember



					mname like '%'||#{word}||'%'


					id like '%'||#{word}||'%'


					email like '%'||#{word}||'%'



		ORDER BY mdate DESC
		)

     )where r >= #{sno} and r <= #{eno}

mapper.update:
update fmember
set fname = #{fname}
where id = #{id}

mapper.select:
select
grade from fmember
where id = #{id}

select
count(*) from fmember
where id = #{id}
and passwd = #{passwd}

SELECT COUNT(id)
FROM fmember
WHERE id=#{id}

SELECT COUNT(email)
FROM fmember
WHERE email=#{email}

SELECT COUNT(*) AS cnt
FROM fmember
WHERE id=#{id} AND passwd=#{passwd}
