mapper.select:
		select ttid, id, name, startdate, enddate, r
		from(
    		select ttid, id, name, startdate, enddate, rownum r
    		from(
    			select ttid, id, name, startdate, enddate
        		from timetable
        	where id = #{id}
    		order by ttid desc
    		)

         )where r >= #{sno} and r <= #{eno}
